import React from 'react';
import ReactDOM from 'react-dom';
import Switch from 'react-switch';
import './index.css';

function Scorebutton(props){
  return(
    <select className="scorebutton" value={props.value} onChange={props.onChange}>
      <option value="1">1</option>
      <option value="2">2</option>
      <option value="3">3</option>
      <option value="4">4</option>
      <option value="5">5</option>
      <option value="6">6</option>
      <option value="7">7</option>
      <option value="8">8</option>
      <option value="9">9</option>
      <option value="10">10</option>
    </select>
  );
}



class Player extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return(
      <div>
         <h3>{this.props.playername}</h3>
         <Scorebutton value="3" onChange={event => this.props.onChange(event)}/>
      </div>
    );
  }
}

class HoleScreen extends React.Component {
  constructor(props) {
    super(props);

    const playerlis  = this.props.players;
    const playerscorelis = [];
    var i;
    for (i = 0; i < playerlis.length; i++){
      playerscorelis.push("3")
    }
    this.state = {
       players: playerlis,
       playerscores: playerscorelis  
    };
  }

  handleChange(event,index){
     const playerscorelis = this.state.playerscores.slice()
     playerscorelis[index] = event.target.value;
     this.setState({
         playerscores: playerscorelis
     });
  }  

  submitScore() {
    alert("Player 2 scored " + this.state.playerscores[1]);
  }
  render() {
    const players1 = this.state.players.slice();
    const playersbuttons = players1.map((player,index) =>
            <li><Player 
                   playername = {player}
                   key={index} 
                   onChange={(event) => this.handleChange(event,index)}
                 /></li>
    );

    return(
      <div>
        <div>
          <h2>Hole {this.props.holenum}</h2>
          <h4>Par {this.props.holepar}</h4>
          <ul className="scoring-list">{playersbuttons}</ul>
        </div>
        <br/>
        <div>
          <button onClick={() => this.submitScore()}>Next Hole</button>
        </div>
      </div>
    );
  }
}

class GameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
       players: ['',''], 
       checked: true
    };
  }

  toggleSwitch = () => {
    this.setState(prevState => {
      return {
        switched: !prevState.switched
      };
    });
  };


  submitPlayers(){
     alert(this.state.players);
  }

  handleChange(event,key) {
    const nplayers = this.state.players.slice();
    nplayers[key] = event.target.value;
    this.setState({
        players: nplayers
    });
  }

  addPlayer() {
    const nplayers = this.state.players.slice();
    nplayers.push("");
    this.setState({
       players: nplayers 
    });
  }

  render() {
    const players1 = this.state.players;
    const playersbuttons = players1.map((player,index) => 
            <li><input 
                   type="text"
                   key={index} 
                   onChange={(event) => this.handleChange(event,index)}
                   value={player}
                 /></li>
    );
    return (
      <div>
        <h1>Working Title</h1>
        <ol>{playersbuttons}</ol>
        <button className="add-player-button" onClick={() => this.addPlayer()}>Add Player</button>
        <br/>
        <label className="toggle-space">
          <span>Front 9</span>
          <Switch 
              className="toggle-switch"
              onChange={this.toggleSwitch} 
              checked={this.state.checked}
              onColor="#223a5e"
              onHandleColor="#8fabd6"
              offColor="#8fabd6"
              offHandleColor="#223a5e"
              handleDiameter={15}
              uncheckedIcon={false}
              checkedIcon={false}
              boxShadow="0px 1px 5px rgba(255, 255, 255, 0.6)"
              activeBoxShadow="0px 0px 1px 10px rgba(255, 255, 255, 0.2)"
              height={24}
              width={40}
              id="small-radius-switch"
          />
          <span>Back 9</span>
        </label>
        <br/>
        <button className="start-game-button" onClick={() => this.submitPlayers()}>Start Game</button>
      </div>
    );
  }
}

ReactDOM.render(
   <div>
     <GameForm />
     <HoleScreen holenum="9" holepar="3"/>
   </div>,
   document.getElementById('root')
);
