import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Scorebutton(props){
  return(
    <select className="scorebutton" value={props.value} onChange={props.onChange}>
      <option value="1">1</option>
      <option value="2">2</option>
      <option value="3">3</option>
      <option value="4">4</option>
      <option value="5">5</option>
      <option value="6">6</option>
      <option value="7">7</option>
      <option value="8">8</option>
      <option value="9">9</option>
      <option value="10">10</option>
    </select>
  );
}



class Player extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return(
      <div>
         <h3>{this.props.playername}</h3>
         <Scorebutton value={this.props.startvalue} onChange={event => this.props.onChange(event)}/>
      </div>
    );
  }
}

class HoleScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
       player1val: "5",
       player2val: "3",
       player3val: "6"
    };
  }

  handleChange(event){
     this.setState({
         player1val: event.target.value
     });
  }  

  submitScore() {
    alert(this.state.player1val);
  }
  render() {
    return(
      <div>
        <div>
          <h2>Hole {this.props.holenum}</h2>
          <h4>Par {this.props.holepar}</h4>
          <Player id="player1" playername="Pearl" startvalue={this.state.player1val} onChange={event => this.handleChange(event)}/>
          <Player id="player2" playername="Todd" startvalue={this.state.player2val}/>
          <Player id="player3" playername="Jenny" startvalue={this.state.player3val}/>
        </div>
        <br/>
        <div>
          <button onClick={() => this.submitScore()}>Next Hole</button>
        </div>
      </div>
    );
  }
}

class GameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
       players: ['','',''] 
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    const players = this.state.players;
    const playersbuttons = players.map((player) => 
            <li><input type="text" onChange={() => this.handleChange()}/></li>
    );
    return (
      <div>
        <h1>Working Title</h1>
        <ol>{playersbuttons}</ol>
        <br/>
        <button onClick={() => this.addPlayer()}>Add Player</button>
        <br/>
        <button onClick={() => this.submitPlayers()}>Start Game</button>
      </div>
    );
  }
}

ReactDOM.render(
   <GameForm />,
   document.getElementById('root')
);
